import {useState,useEffect,useCallback} from "react";
import { BiCalendar} from "react-icons/bi";
import Search from "./components/Search";
import AddAppointment from "./components/AddAppointment";
import AppointmentInfo from "./components/AppointmentInfo";

function App() {

    let [appointmentList,setAppointmentList] = useState([]);
    let [query,setQuery] = useState("")
    let [sortBy,setSortBy] = useState("petName");
    let [orderBy,setOrderBy] = useState("asc");

    const filteredAppointments = appointmentList.filter(
        item => {
            return (
                //  includes method from JavaScript to also take a look at the query and
                //  compare our petName to the query, also converted to lowercase just in case.
                item.petName.toLowerCase().includes(query.toLowerCase()) ||
                item.ownerName.toLowerCase().includes(query.toLowerCase()) ||
                item.aptNotes.toLowerCase().includes(query.toLowerCase())

            )
        }
    ).sort((a,b) => {
        let order = (orderBy === 'asc') ? 1 : -1;
        return (
            a[sortBy].toLowerCase() < b[sortBy].toLowerCase() ? -1 * order: 1 * order
        )

    })

    const fetchData = useCallback(() => {
        fetch('./data.json')
            .then(response => response.json())
            .then(data => {
                setAppointmentList(data)
            })

    },[])

    useEffect(() => {
        fetchData()
    },[fetchData])

  return (
    <div className="App container mx-auto mt-3 font-thin">
     <h1 className="text-5xl mb-3">
         <BiCalendar className="inline-block text-red-400 align-top"/>Your Appointment</h1>
        <AddAppointment/>


        <Search query={query}
                onQueryChange={ (myQuery) => setQuery(myQuery)}/>


        <ul className="divide-y divide-gray-200">
            { filteredAppointments
                .map(appointment => (
                <AppointmentInfo key={appointment.id}
                appoint={appointment}
                onDeleteAppointment={
                    (appointmentId) =>
                        //  JavaScript filter method to remove any items that match the ID that we get past
                        setAppointmentList(appointmentList.filter(appointment =>
                       appointment.id !== appointmentId
                    ))

                }


                />
            ))

            }

        </ul>
    </div>
  );
}

export default App;
